package hccix.hotelcentral1

import android.R
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.*
import android.widget.AdapterView
import android.widget.ArrayAdapter
import android.widget.Toast
import androidx.core.widget.addTextChangedListener
import com.bumptech.glide.Glide
import com.bumptech.glide.load.engine.DiskCacheStrategy
import com.google.android.material.snackbar.Snackbar
import com.google.android.material.textfield.TextInputLayout
import hccix.hotelcentral1.databinding.ActivityHabitacionBinding
import hccix.hotelcentral1.databinding.ActivityMainBinding
import org.jetbrains.anko.doAsync
import org.jetbrains.anko.uiThread
import java.util.*

class HabitacionActivity : AppCompatActivity(),AdapterView.OnItemSelectedListener {
    private lateinit var mBinding: ActivityHabitacionBinding
    private var hActivity:MainActivity?=null
    private  var mhabitaEntity:HabitacionEntity?=null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        mBinding=ActivityHabitacionBinding.inflate(layoutInflater)
        setContentView(mBinding.root)
        //Para el spinner: Tipo de Habitacion:
        val aatipohab= ArrayAdapter<String>(this, R.layout.simple_spinner_dropdown_item)
        aatipohab.addAll(Arrays.asList("Simple","Doble","Triple","Familiar"))
        mBinding.spntipohabita.onItemSelectedListener=this
        mBinding.spntipohabita.adapter= aatipohab
        //Para el spinner:ubicacion
        val aaubicahab= ArrayAdapter<String>(this, R.layout.simple_spinner_dropdown_item)
        aaubicahab.addAll(Arrays.asList("Interior","Exterior"))
        mBinding.spnubicahabita.onItemSelectedListener=this
        mBinding.spnubicahabita.adapter= aaubicahab
        //para recibir desde el main:
        val habitaId=intent.getLongExtra("habiid",0L)
        pintarimagen(habitaId)
        //
        if(habitaId!=null && habitaId!=0L){
            Toast.makeText(this,"Editar HabID: $habitaId", Toast.LENGTH_LONG).show()
            doAsync {
                mhabitaEntity=ReservaAplication.database.habitaDao().findById(habitaId)
            }
        }
        mBinding.etFotourl.addTextChangedListener {
            validatehab(mBinding.ilFotourl)        //actualiza los colores
        }
        mBinding.etDescripcion.addTextChangedListener {
            validatehab(mBinding.ilDescripcion)        //actualiza los colores
        }
        //evento boton flotante
        mBinding.fabGuardar.setOnClickListener {
            guardarHabitacion()
        }
    }

    private fun guardarHabitacion() {
           if(validatehab(mBinding.ilDescripcion,mBinding.ilFotourl)){
                    with(mhabitaEntity!!){      // cargar datos
                        tipo= mBinding.spntipohabita.selectedItem.toString()
                        ubicacion=mBinding.spnubicahabita.selectedItem.toString()
                        fotourl=mBinding.etFotourl.text.toString().trim()
                        descripcion=mBinding.etDescripcion.text.toString().trim()
                    }
                    doAsync {
                        ReservaAplication.database.habitaDao().update(mhabitaEntity!!)
                        uiThread {
                            Snackbar.make(mBinding.root,getString(hccix.hotelcentral1.R.string.mesa_edit_success),
                                Snackbar.LENGTH_LONG).show()
                            //hActivity?.onBackPressed()
                        }
                    }
                }
    }
    //terminar el CVida correctamente o volver al estado original
    override fun onDestroy() {
        //hActivity?.supportActionBar?.setDisplayHomeAsUpEnabled(false)
       // hActivity?.supportActionBar?.title=getString(hccix.hotelcentral1.R.string.app_name)
        super.onDestroy()
        //hActivity?.hideFabNuevo(true)
    }
    private fun pintarimagen(habitaId:Long){
        var url=""
        when (habitaId.toInt()) {
            101 -> url="data:image/jpeg;base64,"
            102 -> url="data:image/jpeg;base64,"
            103 -> url="data:image/jpeg;base64,"
            201 -> url="data:image/jpeg;base64,"
            202 -> url="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRYkQo0KeK1xR4mT_W3Wpg0l6Ve6GyfNxd32g&usqp=CAU"
            203 -> url="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT5baHqw2Wte4HSk2HTOMpgFgyRDTAC6xJBow&usqp=CAU"
            301 -> url="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR8Zt7NXTjmBLG30SO4rs5DD1597DU7EqUIoA&usqp=CAU"
            302 -> url="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS7PXCXsLDKHDjltnghpzWXT3zYgs-9Db5o-Q&usqp=CAU"
            303 -> url="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRFy7jqJGd5nkwEIslsXHsGMpowXsHjCYOanA&usqp=CAU"
            401 -> url="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTaqB59Bu-DI_8ZA-svDo4cCpLW_yOQbcJgvw&usqp=CAU"
            402 -> url="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQuAa7T3yBQdriEWyMyYfa3Zv8O9rNe5DW3FA&usqp=CAU"
            403 -> url="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT6BBPtIebd1TbMtHuPRqky9N7NZicMu5E4ag&usqp=CAU"
        }
        Glide.with(this).load(url).diskCacheStrategy(DiskCacheStrategy.ALL)
            .centerCrop().into(mBinding.imagePhoto)
    }

    private fun validatehab(vararg array: TextInputLayout):Boolean{
        var isValid=true
        for(textField in array){
            if(textField.editText?.text.toString().trim().isEmpty()){
                textField.error=getString(hccix.hotelcentral1.R.string.text_required)
                textField.requestFocus()
                isValid=false
            }
            else{
                textField.error=null     //quita el rojo
            }
        }
        return isValid
    }

    override fun onItemSelected(p0: AdapterView<*>?, p1: View?, p2: Int, p3: Long) {
    }

    override fun onNothingSelected(p0: AdapterView<*>?) {
    }



}